import time
import numpy as np
import Adafruit_TCS34725

import RPi.GPIO as GPIO


GPIO.setmode(GPIO.BOARD)

sensor = Adafruit_TCS34725.TCS34725()
sensor.set_integration_time(0xC0)   # 154 ms
sensor.set_gain(0x03)   # x16 gain


def verkeerslicht(sensor):
    """
    Meet 2 keer 3 seconden waarden op en vergelijkt de magnitude van de roodwaarde voor
    1 Hz. Returnt 'rood' of 'groen'.
    """
    intervalduur = 3
    mag_1Hz = []
    for j in range(2):
        roodwaarden = []
        starttijd = time.time()
        while (time.time() - starttijd) < intervalduur:
            r,g,b,c = sensor.get_raw_data()
            roodwaarden.append(r)
        mag_rood = [np.abs(nummer) for nummer in np.fft.rfft(roodwaarden)]
        freq_mag_rood = np.fft.rfftfreq(intervalduur*7,1/7)

        for i in range(len(freq_mag_rood)):
            if freq_mag_rood[i] == 1:
                mag_1Hz.append(mag_rood[i])
                break

    # Vergelijken
    if mag_1Hz[0] - mag_1Hz[1] > 550 and mag_1Hz[1] < 500 or mag_1Hz[1] < 450:
        return "groen"

    else:
        return "rood"


if __name__ == '__main__':
    while True:
        verkeerslicht(sensor)




